DWAIT .equ 0fch
DCOM .equ 0f8h
DDATA .equ 0fbh
DSTAT .equ 0f8h
DSECT .equ 0fah

main:
    LXI b, diskData1 ;loadB
    mvi d, 0 ;trackNumber
    IN DWAIT


start:
    mvi a, 0xf4
    out DCOM

    ;check for DRQ
loop:
    IN DWAIT

    LXI H, 0xFFB8 ; really bad hardcoded, addr for -diskData2 (2 complement)

    DAD B
    jc loop2_b

    LDAX B
    OUT DDATA
    INX B

    jmp loop

loop2_b:
    mvi a, 0xF0
    out 0xff
    IN DWAIT
    mov a, d
    out DDATA

loop2:
    IN DWAIT
    ORA A
    jp increment
    LDAX B
    OUT DDATA
    INX B
    jmp loop2


increment:
    LXI b, diskData1
    mov a, d
    cpi 0x4c
    jz end
    inr a
    mov d, a
    cma
    out 0xff
    mvi a, 0b01011100
    out DCOM
    IN DWAIT
    jmp start

end:
    hlt

diskData1 db64  
diskData2 db64 
